import logging
import numpy as np

from .target_list_header import Target_List_Header as Header


logging.basicConfig(format='%(levelname)s - %(asctime)s : %(message)s',datefmt='%H:%M:%S',level=logging.DEBUG)

class Target_Data_Packet:
    frame_id = np.uint(0x0000)
    packet_no = np.uint(0x0000)
    
    _packet_length = 1012
    _no_of_target_bytes=24

    def __init__(self,radar_obj,header_farme_id,no_of_packets,no_of_targets,data_arr,no_of_bytes=24):
        self.data_arr = data_arr
        self.radar_obj = radar_obj
        self.no_of_packets = no_of_packets
        self.no_of_targets = no_of_targets
        self.no_of_target_bytes = no_of_bytes
        self.header_farme_id = header_farme_id
    
    def parse(self):
        try:
            if self.data_arr is None or len(self.data_arr) != self._packet_length :
                logging.warning('Invalid data packet')
                return False
            
            if self.no_of_targets <=0 :
                logging.warning('Invalid number of targets')
                return False
            
            
            self.frame_id =int.from_bytes(self.data_arr[0:1], "little")
            self.packet_no =int.from_bytes(self.data_arr[2:3], "little")

            if self.frame_id != self.header_farme_id:
                logging.warning('mismatched incoming frame id ')
                return False

            if self.no_of_packets <= self.packet_no:
                logging.warning('mismatched packet number')
                return False
            logging.info('start parse targets ...')
            for x in range(self.no_of_targets) :
                start_index = 4+x+(x*self._no_of_target_bytes)
                end_index = start_index + self._no_of_target_bytes
                target_data_arr = self.data_arr[start_index : end_index]
                logging.info(f'Target index {x} buffer = {target_data_arr}')
                target_obj = Target_Data(target_data_arr)
                if target_obj.parse() :
                    logging.info('Target parsed ...')
                    logging.info(f'target data : \n RCS ={target_obj.signal_strength} \n range = {target_obj.range} \n velocity ={target_obj.velocity} \n angle azimuth = {target_obj.angle_azimuth} /n ')
                    if self.radar_obj.add_target(target_obj) is False :
                        logging.error('failed to add target in the radar target list')


                else :
                    logging.warning('Failed to parse target from the packet')
                
                print("YYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYYY")

        except Exception as exception_error:
            logging.warning(f'can not parse data packet : {exception_error}')
            return False

    
        

        


class Target_Data:
    signal_strength =0.0
    range = 0.0
    velocity =0.0
    angle_azimuth =0.0
    _packet_length=24
   

    def __init__(self,data_arr,no_of_bytes=24):
        self.data_arr = data_arr
        self._packet_length = no_of_bytes
        
       
    
    def parse(self):
        try:
            if self.data_arr is None or len(self.data_arr) != self._packet_length :
                logging.warning('Invalid target data packet')
                return False

            
            temp_arr = bytearray(self.data_arr[0:4])
            self.signal_strength  = np.frombuffer(temp_arr,dtype='<f') 
          

            temp_arr = bytearray(self.data_arr[4:8])
            self.range = np.frombuffer(temp_arr,dtype='<f') 

            temp_arr = bytearray(self.data_arr[8:12])
            self.velocity = np.frombuffer(temp_arr,dtype='<f') 

            temp_arr = bytearray(self.data_arr[12:16])
            self.angle_azimuth = np.frombuffer(temp_arr,dtype='<f') 
            return True

           
        
        except Exception as exception_error:
            logging.warning(f'can not parse target packet : {exception_error}')
            return False
        



